import React from "react";

const coursesData = [
    {
        id: 1,
        title: "Social Media Management",
        image: "images/courses-image1.jpg",
        author: "Mark Wilson",
        authorImg: "images/author-image1.jpg",
        price: "USD 25",
        date: "12 / 7 / 2018",
        time: "7 Hours"
    },
    {
        id: 2,
        title: "Graphic & Web Design",
        image: "images/courses-image2.jpg",
        author: "Jessica",
        authorImg: "images/author-image2.jpg",
        price: "USD 80",
        date: "20 / 7 / 2018",
        time: "4.5 Hours"
    },
    {
        id: 3,
        title: "Marketing Communication",
        image: "images/courses-image3.jpg",
        author: "Catherine",
        authorImg: "images/author-image3.jpg",
        price: "Free",
        date: "15 / 8 / 2018",
        time: "6 Hours"
    },
    {
        id: 4,
        title: "Summer Kids",
        image: "images/courses-image4.jpg",
        author: "Mark Wilson",
        authorImg: "images/author-image1.jpg",
        price: "USD 45",
        date: "10 / 8 / 2018",
        time: "8 Hours"
    },
    {
        id: 5,
        title: "Business & Management",
        image: "images/courses-image5.jpg",
        author: "Jessica",
        authorImg: "images/author-image2.jpg",
        price: "Free",
        date: "5 / 10 / 2018",
        time: "10 Hours"
    }
];

function Courses() {
    return (
        <section className="container my-5">
            <div className="text-center mb-5">
                <h2>
                    Popular Courses <br />
                    <small className="text-muted">
                        Upgrade your skills with newest courses
                    </small>
                </h2>
            </div>

            <br /><br />

            <div className="row">
                {coursesData.map((course) => (
                    <div className="col-md-6 col-sm-6 mb-4" key={course.id}>
                        <div className="card h-100 shadow">
                            <img src={course.image} className="card-img-top" alt={course.title} />

                            <div className="card-body">
                                <small className="text-muted">
                                    üìÖ {course.date} | ‚è∞ {course.time}
                                </small>
                                <h5 className="mt-2">{course.title}</h5>
                            </div>

                            <br /><br />

                            <div className="card-footer d-flex justify-content-between align-items-center">
                                <div>
                                    <img
                                        src={course.authorImg}
                                        alt={course.author}
                                        style={{ width: "35px", borderRadius: "50%" }}
                                    />
                                    <span className="ms-2">{course.author}</span>
                                </div>
                                <strong>{course.price}</strong>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </section>
    );
}

export default Courses;
